styles:
:host {
    width: 100%;
    display: flex;
    flex-wrap: nowrap;
    flex-direction: column;
    align-content: stretch;
    align-items: stretch;
    justify-content: flex-start;
    overflow: hidden;
}


/* Dont override absolute positioning. This will become unecesary after removing css file. */

:host:not(.sme-layout-absolute) {
    position: relative;
}

th {
    position: relative;
    padding: 0;
}

th .text{
    line-height:27px;
    vertical-align: middle;
    padding: 0 8px;
    width: calc(100% - 5px);
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}

table th:last-of-type {
    padding-left: 0;
}

.header.selectable th {
    cursor: pointer;
}

.header.selectable th .text:hover,
.header.selectable.has-multiple-selection table thead tr th.first .datatable-checkbox:hover {
    background: #f8f8f8;
}

th .sme-icon:before {
    font-size: 11px;
}

.header table thead tr th.first:hover {
    background: none;
    cursor: default;
}

.header table thead tr th.first .datatable-checkbox {
    display: none;
}

.header.has-multiple-selection.show-all-item-checkbox table thead tr th.first .datatable-checkbox {
    display: block;
}

.header table thead tr th.first,
.content .item td.first,
.content .item.no-data .prefix {
    width: 26px;
}

.content .item td.group-header-cell {
    position: relative;
}

.content .item td.group-header-cell .sme-table-cell{
    position: absolute;
    top:0;
    left:0;
    width: 99999px;
    /* Cannot use right:0 to right align the border of group header to the table row 
    because this element's parent is a relative TD element.
    But since the group header is a full row div, so just make it super wide then visually it's still right align to the table row */
}

.header.has-multiple-selection table thead tr th.first,
.content.has-multiple-selection .item td.first,
.content.has-multiple-selection .item.no-data .prefix {
    width: 26px;
    padding-left: 0;
}

.header.has-multiple-selection.show-all-item-checkbox table thead tr th.first,
.content.has-multiple-selection.show-all-item-checkbox .item td.first,
.content.has-multiple-selection.show-all-item-checkbox .item.no-data .prefix {
    width: 47px;    
}

.header.has-multiple-selection.show-all-item-checkbox table thead tr th.first {
    padding-left: 29px;
}

.header table thead tr th.first,
.content .item td.first {
    display: none;
}

.header.selectable.show-left-margin table thead tr th.first,
.content.selectable.show-left-margin .item td.first {
    display: table-cell;
}


/* .header table thead tr th.first,
.item td.first .sme-table-cell {
    padding: 0;
} */

.item td.first .datatable-checkbox, .item td.first .datatable-checkbox input {
    display: none;
}

.tree .item td.first-data-column .datatable-checkbox {
    visibility: hidden;
}

.tree .item.checked td.first-data-column .datatable-checkbox,
.tree .item td.first-data-column:hover .datatable-checkbox {
    visibility: visible;
}

.content.has-multiple-selection .item.checked td.first .datatable-checkbox,
.content.has-multiple-selection .item.checked td.first .datatable-checkbox input,
.content.has-multiple-selection .item td.first:hover .datatable-checkbox,
.content.has-multiple-selection .item td.first:hover .datatable-checkbox input {
    display: block;
}

.item.no-data .prefix {
    float: left;
}

.item.no-data .sme-table-cell {
    padding: 0 8px;
}

.item.group-header .sme-table-cell:first-of-type .sme-icon:before {
    font-size: 10px;
    line-height: 10px;
    margin-top: -3px;
    margin-right: 3px;
}

.item.group-item .first-data-column .sme-table-cell {
    padding-left: 26px;
}

.header table.sme-table-virtualized th:first-of-type {
    padding-left: 8px;
    padding-right: 8px;
}

.header.has-multiple-selection table.sme-table-virtualized th:first-of-type,
.content.has-multiple-selection table.sme-table-virtualized td:first-of-type .sme-table-cell {
    padding-left: 29px;
    padding-right: 0px;
}

/* .header table thead tr th {
    padding: 0 8px;
} */

.header {
    flex: 0 0 auto;
    /* border-bottom: solid 1px #ddd; */
    /* background: white; */
    position: absolute;
    margin-top: -1px;
    z-index: 1;
}

.header.is-scrolled {
    box-shadow: 0px -1px 7px #999;
}

div.content {
    flex: 1 1 auto;
    position: relative;
    margin-top: 27px;
    margin-left: 0;
}

.content.no-header {
    margin-top: 0;
}

.content .scroll-container.sme-layout-absolute.sme-position-inset-none .scroll-data {
    position: absolute;
    left: 0;
    width: 100%;
    overflow: hidden;
    border-collapse: collapse;
}

.content .scroll-container {
    overflow-x: hidden;
    overflow-y: hidden;
}

.content .scroll-container.should-scroll{
    overflow-y: auto;
}

.content .scroll-body {
    position: relative;
    overflow: hidden;
    width: 100%;
}

.sme-table-cell {
    height: 30px;
    line-height: 30px;
    font-size: 12px;
    overflow: hidden;
}

table.sme-table-virtualized td .sme-table-cell,
table.sme-table-virtualized td .sme-table-expander-cell {
    border-bottom: none;
}

.show-grid table.sme-table-virtualized td .sme-table-cell,
.show-grid table.sme-table-virtualized td .sme-table-expander-cell {
    border-bottom: solid 1px #eee;
} 

.content.selectable .item.data {
    cursor: pointer;
}

.content.selectable .item.data:hover {
    background: #f2fbfe;
}

.item.data.selected, .content.selectable .item.data.selected:hover {
    background: #E6F7FE;
}

.item .sme-table-cell {
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    /* padding: 0 8px; */
}

