    width: 100px;
    height: 100px;
    overflow: scroll;
    position: absolute;
    top: -9999px;
}

.datatable-checkbox {
    width: 100%;
    position: relative;
    height: 30px;
}

.tree .datatable-checkbox-container{
    width: 24px;
    display:block;
    float: left;    
    padding-left:4px;
    padding-right:4px;
}

.tree .datatable-checkbox input {
    visibility: hidden;    
}

.tree .datatable-checkbox:hover input{
    visibility: visible;
}

.datatable-checkbox label {
    width: 12px;
    height: 12px;
    cursor: pointer;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: white;
    border: solid 1px #686868;
}

.datatable-checkbox input[type=checkbox]:hover+label {
    background: rgb(242, 251, 254);
}

