    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    margin: 0;
    opacity: 0;
    cursor: pointer;
}

.datatable-checkbox input[type=checkbox]:checked+label {
    background: none;
    border: solid 1px transparent;
}

.datatable-checkbox input[type=checkbox]:focus+label {
    outline: rgba(0, 0, 0, .8) dashed 1px;    
}

.datatable-checkbox input[type=checkbox]:checked+label:after {
    content: '\E8FB';
    font-family: Server-MDL2;
    position: absolute;
    top: 0;
    line-height: 12px;
}

.loading-indicator {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
}

.tree .scroll-data .first-data-column .sme-table-expander-cell {
    float: left;
    height: 30px;    
}

.tree .scroll-data .first-data-column .sme-table-cell {
    padding-left: 0;
}

:host>>>.tree .scroll-data .sme-table-cell .sme-icon:before {
    margin-top: -4px;
}

:host>>>.tree .scroll-data .sme-table-cell .placeholder {
    display: inline-block;
    width: 16px;
}

.tree .scroll-data .first-data-column .sme-table-expander-cell .sme-icon {
    visibility: hidden;    
}

.tree.has-multiple-selection .scroll-data .first-data-column .sme-table-expander-cell .sme-icon {
    margin-left: 2px;
}

.tree .scroll-data .first-data-column .sme-table-expander-cell.has-children .sme-icon {
    visibility: visible;
    float:left;
    width: 24px;
    text-align: center;
}

.tree.has-multiple-selection .scroll-data .first-data-column .sme-table-expander-cell.has-children .sme-icon {
    width: 10px;
    margin-left: 5px;
}

.tree .scroll-data .first-data-column .sme-table-expander-cell .placeholder {
    display: block;
    width: 24px;
}

.tree.has-multiple-selection .scroll-data .first-data-column .sme-table-expander-cell .placeholder{
    width: 10px;
}

.tree .scroll-data .first-data-column .sme-table-expander-cell.has-children .placeholder {
    display: none;
}

