styles:
/** Note: the ":host >>>" syntax allows us to apply a class down
 *  through our elements dom tree and escape angulars scoping restrictions.
 *  editors dont like >>> yet because it is fairly new syntax. Angular takes care of this for us though and should not cause any compatibility concerns
 *  See https://angular.io/docs/ts/latest/guide/component-styles.html for more details. (search for "/deep/")
 */


/* combobox */

:host>>>.combobox {
    width: 100%;
    max-width: 498px;
    min-width: 67px;
}

:host>>>.combobox:after {
    /* bootstrap combox after consumes the click event and is selectable, so we disable both*/
    pointer-events: none;
    user-select: none;
}


/* nav-tabs */

:host>>>.nav-tabs {
    margin-left: 0;
    margin-right: 0;
}

:host>>>.nav-tabs>li {
    padding: 0 10px;
}

:host>>>.nav-tabs.paddless>li:first-of-type {
    padding: 0 10px 0 0;
}

:host>>>.nav-tabs.paddless>li:last-of-type {
    padding: 0 0 0 10px;
}

:host>>>.nav-tabs>li>a {
    font-weight: 400;
    font-size: 15px;
    padding: 0 2px;
    color: rgba(0, 0, 0, 0.6);
    cursor: pointer;
    user-select: none;
}

:host>>>.nav-tabs>li>a:hover,
 :host>>>.nav-tabs>li>a:focus {
    color: rgba(0, 0, 0, 0.8);
    /*font-family: "Segoe UI",Selawik,Tahoma,Verdana,Arial,sans-serif;*/
}

:host>>>.nav-tabs>li>a.active,
:host>>>.nav-tabs>li>a.active:hover,
:host>>>.nav-tabs>li>a.active:focus,
:host>>>.nav-tabs>li.active>a,
:host>>>.nav-tabs>li.active>a:hover,
:host>>>.nav-tabs>li.active>a:focus {
    border-bottom: 2px solid #0078d7;
    color: #000;
    font-weight: bold;
}

:host>>>.tab-content {
    padding: 10px;
}

:host>>>form,
 :host>>>form label,
 :host>>>form p,
 :host>>>form.theme-default {
    color: inherit;
}


/* buttons */

:host>>>.btn {
    background: rgba(0, 0, 0, 0.2);
    border: 1px solid transparent;
    color: #000;
    outline: transparent dashed 1px;
    outline-offset: 0px;
    transition: scale .2s ease-in-out;
    margin: 0;
}

:host>>>.btn:hover,
:host>>>.btn:focus,
:host>>>.btn:active {
    background: rgba(0, 0, 0, 0.3);
    outline-offset: 0px;
    box-shadow: none;
}

:host>>>.btn:active {
    background: rgba(0, 0, 0, 0.4);
    -webkit-transform: scale(0.98);
    -ms-transform: scale(0.98);
    transform: scale(0.98);
}

:host>>>.btn.btn-primary {
    background: #0067b8;
    border: 1px solid transparent;
    color: #fff;
}

:host>>>.btn.btn-primary:hover,
:host>>>.btn.btn-primary:focus {
    background: #005da6;
}

:host>>>.btn.btn-primary:active {
    background: #005293;
}

:host>>>.checkbox.toggle-switch label {
    margin-left: 48px;
}

:host>>>.checkbox.toggle-switch label span::before {
    border-radius: 10px;
    width: 40px;
    font-size: 45px;
    padding-top: 1px;
    line-height: 0;
    text-indent: 0;
    content: "\2022";
}

:host>>>.checkbox.toggle-switch label input:checked~span::before {
    text-indent: 21px;
}

:host>>>.checkbox.toggle-switch label input:not(:checked)~span::before {
    padding-top: 4px;
}

:host>>>.checkbox input[type=checkbox]:focus+span::before {
    outline: rgba(0, 0, 0, .8) dashed 1px;
}/** Note: the ":host >>>" syntax allows us to apply a class down
 *  through our elements dom tree and escape angulars scoping restrictions.
 *  editors dont like >>> yet because it is fairly new syntax. Angular takes care of this for us though and should not cause any compatibility concerns
 *  See https://angular.io/docs/ts/latest/guide/component-styles.html for more details. (search for "/deep/")
 */


/*
    Positioning helpers
*/

:host>>>.stretch-absolute {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
}

:host>>>.stretch-fixed {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
}

:host>>>.stretch-vertical {
    height: 100%;
}

:host>>>.relative {
    position: relative;
}

:host>>>.relative-center {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translateX(-50%) translateY(-50%);
}


/*
    Form Helpers
*/

:host>>>fieldset {
    border: 0;
    padding: 0;
    margin: 0;
}

:host>>>form {
    display: flex;
    flex-wrap: nowrap;
    flex-direction: column;
    align-content: stretch;
    align-items: stretch;
    justify-content: flex-start;
    /*font-size: 15px;*/
}

:host>>>.form-group .form-group-label,
:host>>>.form-group label {
    font-size: inherit;
}

:host>>>form .indent {
    margin-left: 24px;
}

:host>>>form .form-controls {
    flex: 1 1 auto;
}

:host>>>form .form-buttons {
    flex: 0 0 auto;
    display: flex;
    flex-wrap: nowrap;
    flex-direction: row;
    align-content: stretch;
    align-items: stretch;
    justify-content: flex-end;
    padding-top: 24px;
    padding-bottom: 24px;
}

:host>>>form .disabled label,
:host>>>form .disabled p,
:host>>>form .disabled h1,
:host>>>form .disabled h2,
:host>>>form .disabled h3,
:host>>>form .disabled h4,
:host>>>form .disabled h5,
:host>>>form .disabled h6,
:host>>>form .disabled .checkbox label span {
    opacity: .8;
}

:host>>>.form-control[disabled],
:host>>>.form-control[readonly],
:host>>>fieldset[disabled] .form-control {
    color: rgba(0, 0, 0, .2);
    border-color: rgba(0, 0, 0, .2);
    background-color: rgba(255, 255, 255, .2);
}

:host>>>form .disabled .checkbox label {
    opacity: 1;
}

:host>>>.form-input {
    margin-bottom: 22px;
    display: block;
}

:host>>>.form-input:last-of-type {
    margin-bottom: 0px;
}

:host>>>.form-input .form-control {
    margin-top: 5px;
    margin-bottom: 0;
    max-width: none;
}

:host>>>.form-input .combobox {
    margin-top: 5px;
    margin-bottom: 0;
    max-width: none;
}

:host>>>.form-input .required-clue {
    /*font-size: 15px;*/
    font-weight: 300;
    padding: 0;
    float: right;
}

:host>>>.form-input .ng-valid[required]:not(.ng-untouched),
:host>>>.form-input .ng-valid.required:not(.ng-untouched) {
    border: 1px solid #107c10;
    /* green */
}

:host>>>.form-input .invalid.ng-dirty,
:host>>>.form-input .ng-invalid.ng-dirty {
    border: 1px solid #d02e00 !important;
    /* red */
}

:host>>>.form-group {
    max-width: 498px;
    margin-bottom: 24px;
}

:host>>>.form-group.form-stretch {
    max-width: none;
}

:host>>>.form-group:last-child {
    margin-bottom: 0px;
}

:host>>>.alert {
    margin-bottom: 0;
    padding: 10px 0 0 0;
    min-height: 20px;
    color: #d02e00;
    background-color: transparent;
}

:host>>>.alert-danger {
    background-color: transparent;
}

:host>>>.delete-button {
    cursor: pointer;
    width: auto;
    height: 36px;
    line-height: 36px;
    overflow: hidden;
    text-overflow: ellipsis;
    padding: 0 12px;
    position: static;
    text-decoration: none;
    text-transform: none;
    text-align: center;
    vertical-align: middle;
    white-space: nowrap;
    float: right;
    display: inline-flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    align-content: center;
}

:host>>>.delete-button:hover {
    background-color: rgba(203, 195, 190, 1);
}

:host>>>.item-list {
    padding-left: 12px;
}

:host>>>.item-list .form-group {
    margin-top: 10px;
}

:host>>>.item-list:hover {
    background-color: rgba(242, 242, 242, 1);
}

:host>>>.radio label span:before {
    background-color: white
}

:host>>>.delete-button.sme-icon:before {
    padding-right: 10px;
}


/*
    Scroll/Overflow Helpers
*/

:host>>>.no-scroll {
    overflow-x: hidden;
    overflow-y: hidden;
}

:host>>>.vertical-scroll-only {
    overflow-x: hidden;
    overflow-y: auto;
}

:host>>>.vertical-scroll-only.modal-scrollable {
    height: 50vh;
}


/*
    Flexable box layout helpers
*/

:host>>>.flex-layout {
    display: flex;
    flex-wrap: nowrap;
    flex-direction: row;
    align-content: stretch;
    align-items: stretch;
    justify-content: flex-start;
}

:host>>>.flex-layout.reverse {
    justify-content: flex-end;
}

:host>>>.flex-layout.vertical {
    flex-direction: column;
}

:host>>>.flex-layout.vertical.multi-section>* {
    margin-bottom: 24px;
}

:host>>>.flex-layout.vertical.multi-section:last-child {
    margin-bottom: 0;
}

:host>>>.flex-layout.wrap {
    flex-wrap: wrap;
}

:host>>>.flex-layout.nowrap {
    flex-wrap: nowrap;
}

:host>>>.auto-flex-size {
    flex: 1 1 auto;
}

:host>>>.fixed-flex-size {
    flex: 0 0 auto;
}

:host>>>.flex-layout.vertical header {
    /*mimics primeng grid header*/
    padding: 4px 10px;
    font-weight: bold;
    color: #222;
}

:host>>>.flex-layout.vertical .ui-datatable-header header {
    padding-left: 0;
    padding-right: 0;
}


/*
    Container types
*/

:host>>>.tool-container {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    overflow-y: auto;
    overflow-x: hidden;
}

:host>>>.tool-container.white-space {
    padding-top: 12px;
    padding-right: 24px;
    padding-left: 24px;
}

:host>>>.dashboard {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 20px 34px;
    overflow-x: hidden;
    overflow-y: auto;
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    align-content: flex-start;
    align-items: flex-start;
    justify-content: flex-start;
    background: #eee;
}

:host>>>.right-panel {
    width: 500px;
    margin-left: 15px;
}


/*
    Border Helpers
*/

:host>>>.border-left {
    border-left: 1px solid #ddd;
}

:host>>>.border-right {
    border-right: 1px solid #ddd;
}

:host>>>.border-top {
    border-top: 1px solid #ddd;
}

:host>>>.border-bottom {
    border-bottom: 1px solid #ddd;
}

:host>>>.border-horizontal {
    border-style: solid;
    border-color: #ddd;
    border-width: 0 1px;
}

:host>>>.border-vertical {
    border-style: solid;
    border-color: #ddd;
    border-width: 1px 0;
}

:host>>>.border-all {
    border-style: solid;
    border-color: #ddd;
    border-width: 1px;
}


/*
    Highlight
*/

:host>>>.highlight {
    background: yellow;
}

:host>>>.disabled .checkbox label span::before,
 :host>>>.disabled.radio label span::before {
    cursor: not-allowed;
}

:host>>>input[type="checkbox"][disabled] + span::before {
  cursor: not-allowed;
  border-color: rgba(0,0,0,.2);
  color: rgba(0,0,0,.2);
}

/*
    Error Message Helpers - TODO: create error message component
*/

:host>>>.error-cover {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    background: rgba(0, 0, 0, 0.6);
    overflow-y: auto;
    overflow-x: hidden;
    z-index: 1;
}

:host>>>.error-message {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translateX(-50%) translateY(-50%);
    color: #fff;
    text-align: center;
    max-width: calc(100% - 30px);
}


/*
    Search Helpers
*/

:host>>>.searchbox {
    flex: 0 0 auto;
    color: #2D3239;
    margin: 5px 0px;
    padding: 0 0 0 4px;
    border: 1px solid;
    border-color: #B3B3B3;
    height: 30px;
    display: flex;
    flex-direction: row;
    flex-wrap: nowrap;
    align-content: stretch;
    align-items: center;
    justify-content: flex-start;
    font-weight: normal;
    font-size: 14px;
}

:host>>>.searchbox::after {
    content: "\E721";
    font-size: 15px;
    margin: 4px 6px;
    font-family: Server-MDL2;
    font-style: normal;
    font-variant: normal;
    font-weight: normal;
    line-height: 14px/1;
    text-rendering: auto;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

:host>>>.searchbox input[type=search] {
    display: block;
    width: calc(100% - 30px);
    overflow: hidden;
    text-overflow: ellipsis;
}

:host>>>.searchbox input[type=search],
:host>>>.searchbox input[type=search]:focus,
:host>>>.searchbox input[type=search]:active,
:host>>>.searchbox input[type=search]:hover {
    border: none;
    outline: none;
}

:host>>>.searchbox-action-bar {
    width: 276px;
    height: 36px;
    align-items: center;
    padding-left: 6px;
    margin-top: 0px;
    margin-bottom: 0px;
    margin-right: 34px;
}


/*
    Table Helpers
*/

:host>>>.table-sm tr {
    height: 30px;
}


/*
    Toolbar component styles
*/

:host>>>ul.toolbar {
    height: 34px;
    color: #327cd4;
    background-color: #e6e6e6;
    overflow: hidden;
    list-style-type: none;
    margin: 0;
    line-height: 34px;
    padding: 0;
}

ul.toolbar {
    margin: 0 0 0 0;
}

:host>>>ul.toolbar li {
    float: left;
    padding: 0;
    margin: 0 24px 0 0;
    vertical-align: middle;
    user-select: none;
    -ms-user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
}

:host>>>ul.toolbar button {
    float: left;
    padding: 0 10px;
    vertical-align: middle;
    -ms-user-select: none;
    -webkit-user-select: none;
    -moz-user-select: none;
    border: 0;
    background-color: transparent;
}

:host>>>ul.toolbar button:hover {
    background-color: #327cd4;
    color: White;
}

:host>>>ul.toolbar button:disabled {
    background-color: transparent;
    color: #808080;
}

:host>>>ul.toolbar button:disabled:hover {
    background-color: transparent;
    color: #808080;
}

:host>>>span.svg-16px svg {
    height: 16px;
    width: 16px;
}

:host>>>ul.toolbar svg {
    margin-right: 5px;
    height: 34px;
    padding: 9px 0;
    vertical-align: top;
}

:host>>>ul.toolbar button:hover svg {
    filter: invert();
}

:host>>>ul.toolbar button:disabled svg {
    filter: opacity(30%);
}

:host>>>ul.toolbar button:disabled:hover svg {
    filter: opacity(30%);
}

:host>>>ul.toolbar> :last-child {
    margin: 0 0 0 0;
}

:host>>>ul.toolbar> :first-child {
    margin-left: 10px;
}


/*
    Tiles
*/

:host>>>.tile-layout {
    overflow: hidden;
}

:host>>>.tile {
    position: relative;
    float: left;
    margin: 0 5px 5px 0;
    background: white;
}

:host>>>.tile .flag {
    position: absolute;
    top: 0;
    left: 0;
    height: 34px;
    width: 34px;
    background-size: contain;
    background-position: center;
    background-repeat: no-repeat;
}

:host>>>.tile-content {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    padding: 20px;
    display: flex;
    flex-wrap: nowrap;
    flex-direction: column;
    align-content: stretch;
    align-items: stretch;
    justify-content: flex-start;
}

:host>>>.tile-header {
    flex: 0 0 auto;
    text-transform: uppercase;
    font-size: 12px;
    color: #808080;
    margin-bottom: 20px;
}

:host>>>.tile-body {
    flex: 1 1 auto
}

:host>>>.tile-footer {
    flex: 0 0 auto;
    font-size: 12px;
}

:host>>>.tile-footer a {
    color: #808080;
}

:host>>>.tile a {
    cursor: pointer;
}


/*
    Tile size calculation: (base * units) + ((base-1) * margin)

    base = 125
    margin = 5
    units = x in size-v-x or size-h-x

    v = vertical
    h = horizontal
*/

:host>>>.tile.size-v-1 {
    height: 125px;
}

:host>>>.tile.size-v-2 {
    height: 255px;
}

:host>>>.tile.size-v-3 {
    height: 385px;
}

:host>>>.tile.size-v-4 {
    height: 515px;
}

:host>>>.tile.size-h-1 {
    width: 125px;
}

:host>>>.tile.size-h-2 {
    width: 255px;
}

:host>>>.tile.size-h-3 {
    width: 385px;
}

:host>>>.tile.size-h-4 {
    width: 515px;
}

:host>>>.tile.size-h-full {
    width: 100%;
}

:host>>>.tile.size-h-half {
    width: 50%;
}

:host>>>.tile-table {
    font-size: 12px;
    width: 100%;
    color: #505050;
    margin-top: 10px;
}

:host>>>.tile-table th {
    font-size: 12px;
    color: #808080;
}

:host>>>.tile-table tr {
    height: 28px;
}

:host>>>.tile-table td {
    padding-right: 5px;
}

:host>>>.tile-table .status-icon {
    padding-left: 16px;
    background-position: left center;
    background-size: 16px;
    background-repeat: no-repeat;
}


/*Rollups: TODO: Make this into a control*/

:host>>>.rollup {
    display: inline-block;
}

:host>>>.rollup:not(:first-of-type) {
    margin-left: 15px;
}

:host>>>.rollup.block:not(:first-of-type) {
    margin-top: 15px;
}

:host>>>.rollup.block {
    display: block;
    margin-left: 0;
}

:host>>>.rollup .rollup-status {
    display: inline-block;
    width: 28px;
    height: 28px;
    background-position: center;
    background-size: contain;
    background-repeat: no-repeat;
}

:host>>>.rollup .rollup-value {
    display: inline-block;
    font-size: 42px;
    line-height: 36px;
    color: #505050;
}

:host>>>.rollup .rollup-title {
    font-size: 10px;
    color: #808080;
    margin-left: 36px;
}

:host>>>.rollup.block .rollup-title {
    margin-left: 0;
}


/*
    Usage Chart: TODO: Make this into a control
*/

:host>>>.usage-chart {
    font-size: 12px;
    display: flex;
    flex-wrap: nowrap;
    flex-direction: row;
    align-content: stretch;
    align-items: stretch;
    justify-content: flex-start;
}

:host>>>.usage-bar-area {
    flex: 1 1 auto;
    max-width: 50%;
}

:host>>>.usage-message-area {
    flex: 0 0 auto;
    max-width: 45%;
}

:host>>>.font-bold,
:host>>>.usage-bar-title,
.usage-message-title {
    font-weight: bold;
}

:host>>>.usage-bar-value,
.usage-message {
    color: #808080;
}

:host>>>.usage-bar-background {
    background: #eaeaea;
    height: 5px;
    margin-top: 5px;
    margin-right: 15px;
    position: relative;
}

:host>>>.usage-bar {
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    background: #0071D6;
}

:host>>>.usage-bar.critical {
    background: #c90000;
}

:host>>>.usage-bar.warning {
    background: #FFC926;
}

:host>>>.usage-bar.acceptable {
    background: #00BD42;
}


/**
 * Details panel styles
 */

:host>>>.details-panel {
    display: flex;
    flex-direction: column;
    flex-wrap: wrap;
    justify-content: flex-start;
    align-items: flex-start;
    align-content: flex-start;
    overflow-y: auto;
    overflow-x: hidden;
}

:host>>>.details-panel.horizontal {
    flex-direction: row;
}

:host>>>.details-panel.flat {
    flex-wrap: nowrap;
    min-height: auto;
    bottom: auto;
}

:host>>>.details-panel>* {
    width: 276px;
    margin-right: 12px;
    margin-bottom: 24px;
    font-size: 13px;
    line-height: 16px;
    overflow-wrap: break-word;
    word-break: break-all;
}

:host>>>.details-panel.stretch-width>* {
    width: 100%;
}

:host>>>.details-panel>*>label {
    color: #4f4f4f;
    font-weight: normal;
    font-size: 11px;
    margin-bottom: 8px;
}


/* put this in the div for the details pane on a tool */

:host>>>.details-panel-container {
    background: #f2f2f2;
    border-top: 1px solid #ddd;
    position: relative;
    padding-top: 12px;
    padding-left: 24px;
    padding-right: 24px;
}


/* select any details-panel-container that is a sibling of the first details-panel-container and turn off the top border*/

:host>>>.details-panel-container~.details-panel-container {
    border-top: none;
}


/* Fill in loading wheel background for details panel */

:host>>>.details-panel-container .progress-cover {
    background: #f2f2f2;
    margin-left: -24px;
    margin-right: -24px;
}

:host>>>.overflow-tool .progress-cover {
    margin-left: -24px;
    margin-right: -24px;
}


/* add this to details-panel content if it is inside a details-panel-container */

:host>>>.details-content {
    padding-left: 24px;
}


/* for tab panels in the details pane */

:host>>>.details-tab {
    padding-left: 0px;
    padding-bottom: 12px;
}

:host>>>.nav-tabs.details-tab>li {
    padding-left: 0px;
    padding-right: 20px;
}


/* primeng datatable styles */

:host>>>.overflow-margins {
    padding: 0 34px;
}

:host>>>.details-panel-container .overflow-margins .ui-treetable thead th.ui-state-default:first-of-type,
:host>>>.details-panel-container .overflow-margins .ui-treetable tbody td:first-of-type:not(.ui-treetable-child-table-container) {
    padding-left: 42px;
}

:host>>>.details-panel-container .overflow-margins .ui-treetable thead th.ui-state-default:last-of-type,
:host>>>.details-panel-container .overflow-margins .ui-treetable tbody td:last-of-type:not(.ui-treetable-child-table-container) {
    padding-right: 42px;
}


/* primeng checkbox background color override
   primeng override css doesn't take the same */

